import axios from 'axios';

export const registerUser = (user) => async dispatch => {
    dispatch({
        type: 'USER_REGISTER_REQUEST'
    })

    try {
        const res = await axios.post(`${process?.env?.REACT_APP_SERVER_URL}/stuReg`, user);
        console.log(res);
        dispatch({
            type: 'USER_REGISTER_SUCCESS'

        })
    } catch (error) {
        dispatch({
            type: 'USER_REGISTER_FAILED',
            payload: error
        })
    }
}
export const registerTeacher = (user) => async dispatch => {
    dispatch({
        type: 'USER_REGISTER_REQUEST'
    })

    try {
        const res = await axios.post(`${process?.env?.REACT_APP_SERVER_URL}/teachReg`, user);
        console.log(res);
        dispatch({
            type: 'USER_REGISTER_SUCCESS'

        })
    } catch (error) {
        dispatch({
            type: 'USER_REGISTER_FAILED',
            payload: error
        })
    }
}


export const loginUser = (user,type) => async dispatch => {
    dispatch({
        type: 'USER_LOGIN_REQUEST'
    })
   
    try {
        var  res
       
        if(type == "Student" || type == "Admin") {
             if(type == "Student" && user.email == 'admin@gmail.com'){
                return ;
             }
          res =  await axios.post(`${process?.env?.REACT_APP_SERVER_URL}/StuSign`, user);
         
        } else if (type=="Admin") {
            console.log("adim","sdfgffdddddddf")
            // res = await axios.post(`${process?.env?.REACT_APP_SERVER_URL}/Signin", user);
            
        } else if (type=="Teacher") {
            res = await axios.post(`${process?.env?.REACT_APP_SERVER_URL}/TecSign`, user);
        }
        
        
        
        
        dispatch({
            type: 'USER_LOGIN_SUCCESS',
            payload: res?.data
        })
        console.log(res?.data)
        localStorage.setItem("jwt", res.data.token);
        localStorage.setItem('currentUser', JSON.stringify(res.data));
        console.log(   type === "Teacher")
        if(type == "Teacher"){
            window.location.href = "/teacher/dashboard";
        } 
        if(type == "Student") {
            window.location.href = "/student/dashboard";
        }
        if(type == "Admin"){
            console.log(type,"adddd")
            localStorage.setItem("admin", type);
            window.location.href = "/admin/dashboard";
        }
        
    } catch (error) {
        dispatch({
            type: 'USER_LOGIN_FAILED',
            payload: error
        })
    }
}





export const logoutUser = () => async dispatch => {

    localStorage.removeItem('currentUser');
    window.location.href = "/landing";

}


export const userProfile = (id,type="other") => async (dispatch, getState) => {
    dispatch({
        type: "USER_PROFILE_REQUEST"
    });
    // const config = {
    //     headers: {
    //         "Content-Type": "application/json",
    //         Authorization: "Bearer " + localStorage.getItem("jwt"),
    //     },
    // };

    var user_type ;

    if(type == "Teacher"){
        user_type = "tprofile"
    }else{
        user_type = "profile"
    }

    console.log(id)

    try {
        const response = await axios.post(`${process?.env?.REACT_APP_SERVER_URL}/${user_type}`,{id});
        console.log(response);
        dispatch({
            type: "USER_PROFILE_SUCCESS",
            payload: response.data,
        });
    } catch (error) {
        dispatch({
            type: "USER_PROFILE_FAILED",
            payload: error,
        });
    }

};



